// Consolider les indisponibilités de Google Calendar et Notion

// Récupération des événements Google Calendar
const gcalEvents = $input.item(0).json.items || [];

// Récupération des indisponibilités Notion
const notionDispo = $input.item(1).json.results || [];

// Conversion des événements Google Calendar en format standard
const gcalIndisponibilites = gcalEvents.map(event => {
  // Déterminer la date (pour les événements sur une journée entière)
  let date = null;
  if (event.start.date) {
    date = event.start.date;
  } else if (event.start.dateTime) {
    // Pour les événements avec heure, prendre juste la date
    date = event.start.dateTime.split('T')[0];
  }

  return {
    date,
    source: 'gcal',
    title: event.summary || 'Événement sans titre',
    id: event.id
  };
}).filter(item => item.date !== null);

// Conversion des indisponibilités Notion en format standard
const notionIndisponibilites = notionDispo.map(page => {
  // Extraire les propriétés pertinentes
  const properties = page.properties;

  // Récupérer la date
  let date = null;
  if (properties.Date && properties.Date.date && properties.Date.date.start) {
    date = properties.Date.date.start;
  }

  // Récupérer le statut (disponible ou non)
  let status = 'unknown';
  if (properties.Dispo && properties.Dispo.status && properties.Dispo.status.name) {
    status = properties.Dispo.status.name === 'Pas dispo' ? 'unavailable' : 'available';
  }

  // Récupérer le membre concerné
  let member = 'Inconnu';
  if (properties.Name && properties.Name.title && properties.Name.title.length > 0) {
    const titleText = properties.Name.title[0].text.content;
    // Extraire le nom du membre (supposant un format comme "John indisponible")
    member = titleText.replace(' indisponible', '').trim();
  }

  return {
    date,
    source: 'notion',
    status,
    member,
    id: page.id
  };
}).filter(item => item.date !== null && item.status === 'unavailable');

// Consolider toutes les indisponibilités
const toutesIndisponibilites = [
  ...gcalIndisponibilites,
  ...notionIndisponibilites
];

