// Calculer les plages libres pour les vendredis et samedis

// Récupérer les indisponibilités consolidées
const indisponibilites = $json.data || [];
const indisponibilitesParDate = $json.byDate || {};

// Définir la période à analyser (3 mois à partir d'aujourd'hui)
const dateDebut = new Date();
const dateFin = new Date();
dateFin.setMonth(dateFin.getMonth() + 3);

// Fonction pour formater une date en YYYY-MM-DD
function formatDate(date) {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
}

// Fonction pour vérifier si une date est un vendredi ou un samedi
function estVendrediOuSamedi(date) {
  const jour = date.getDay();
  return jour === 5 || jour === 6; // 5 = vendredi, 6 = samedi
}

// Générer toutes les dates de la période
const toutesLesDates = [];
const currentDate = new Date(dateDebut);

while (currentDate <= dateFin) {
  if (estVendrediOuSamedi(currentDate)) {
    toutesLesDates.push(formatDate(currentDate));
  }
  currentDate.setDate(currentDate.getDate() + 1);
}

// Filtrer les dates disponibles (celles qui ne sont pas dans les indisponibilités)
const datesDisponibles = toutesLesDates.filter(date => {
  // Si la date n'est pas dans les indisponibilités, elle est disponible
  return !indisponibilitesParDate[date] || indisponibilitesParDate[date].length === 0;
});

// Formater les résultats
const plagesLibres = datesDisponibles.map(date => {
  // Convertir la date en objet Date pour extraire le jour de la semaine
  const dateObj = new Date(date);
  const jourSemaine = ['Dimanche', 'Lundi', 'Mardi', 'Mercredi', 'Jeudi', 'Vendredi', 'Samedi'][dateObj.getDay()];

  return {
    date,
    jourSemaine,
    disponible: true
  };
});

// Retourner les plages libres
return {
  json: {
    data: plagesLibres,
    count: plagesLibres.length,
    period: {
      start: formatDate(dateDebut),
      end: formatDate(dateFin)
    }
  }
};
