// Préparation du prompt pour DeepSeek

// Récupérer les dates disponibles de la Phase 1
const plagesLibres = $json.data || [];

// Formater les dates pour le prompt
const datesFormatees = plagesLibres.map(obj => `- ${obj.date} (${obj.jourSemaine})`).join('\n');

// Construire le prompt pour l'IA
const prompt = `Tu es un assistant spécialisé dans la rédaction d'emails professionnels pour un groupe de musique en tournée. 

Tu dois rédiger un email pour proposer des dates de concert à des salles et organisateurs. 

Voici les dates disponibles pour le groupe :
${datesFormatees}

Rédige un email professionnel, chaleureux mais concis qui :
1. Présente brièvement le groupe Gribitch (musique électro-acoustique, influences trip-hop et jazz)
2. Propose les dates ci-dessus pour une potentielle représentation
3. Mentionne que le groupe peut s'adapter aux contraintes techniques
4. Demande poliment une réponse, même en cas de refus

L'email doit faire environ 10-15 lignes maximum. N'inclus pas d'objet ni de signature.`;

// Préparer la requête pour OpenRouter (DeepSeek)
return {
  json: {
    prompt,
    model: "deepseek-ai/deepseek-v3",
    messages: [
      {
        role: "system",
        content: "Tu es un assistant spécialisé dans la rédaction d'emails professionnels pour un groupe de musique en tournée."
      },
      {
        role: "user",
        content: prompt
      }
    ]
  }
};

// Préparation de la requête pour DeepSeek via OpenRouter

// Récupérer le prompt préparé
const prompt = $json.prompt;
const model = $json.model || "deepseek-ai/deepseek-v3";
const messages = $json.messages || [];

// Construire la requête pour l'API OpenRouter
return {
  json: {
    requestData: {
      url: "https://openrouter.ai/api/v1/chat/completions",
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer ",
        "HTTP-Referer": "https://n8n.io",
        "X-Title": "Email Sender Workflow"
      },
      body: {
        model: model,
        messages: messages,
        temperature: 0.7,
        max_tokens: 1000
      }
    }
  }
};

// Fusion des contacts LOT1 avec les lieux

// Récupérer les contacts de la Phase 1
const contacts = $input.item(1).json.results || [];

// Formater les contacts avec les informations nécessaires
const contactsFormated = contacts.map(contact => {
  const properties = contact.properties;

  // Extraire les informations pertinentes
  const name = properties.Name?.title[0]?.text?.content || 'Contact sans nom';
  const email = properties.Email?.email || '';
  const venue = properties.Venue?.rich_text[0]?.text?.content || 'Salle non spécifiée';
  const region = properties.Region?.select?.name || 'Région non spécifiée';
  const status = properties.Status?.status?.name || 'Nouveau';

  // Ne garder que les contacts avec email et qui n'ont pas déjà été contactés
  if (!email || status !== 'Nouveau') {
    return null;
  }

  return {
    id: contact.id,
    name,
    email,
    venue,
    region,
    status
  };
}).filter(contact => contact !== null);

// Retourner les contacts formatés
return {
  json: {
    contacts: contactsFormated,
    count: contactsFormated.length
  }
};

// Fusion du message IA avec les contacts

// Récupérer le message généré par l'IA
const messageIA = $input.item(0).json.message || '';

// Récupérer les contacts formatés
const contacts = $input.item(1).json.contacts || [];

// Créer un item pour chaque contact avec le message IA
return contacts.map(contact => ({
  json: {
    ...contact,
    messageIA
  }
}));

// Personnalisation du message pour chaque contact

// Récupérer les données du contact et le message IA
const contact = $json;
const messageIA = $json.messageIA || '';
const name = $json.name || 'Responsable';
const venue = $json.venue || 'votre salle';

// Personnaliser le message
const messagePersonnalise = `Bonjour ${name},

${messageIA}

Au plaisir d'échanger avec vous,
L'équipe Gribitch
`;

// Retourner le message personnalisé avec les informations du contact
return {
  json: {
    ...contact,
    message: messagePersonnalise,
    to: $json.email,
    subject: `Proposition – Groupe en tournée (automatisé)`
  }
};

// Injection du message dans le template HTML

// Récupérer le template HTML
const htmlTemplate = $input.item(0).json.body || '';

// Récupérer le message personnalisé
const message = $input.item(1).json.message || '';

// Convertir le message texte en HTML (remplacer les sauts de ligne par des <br>)
const messageHtml = message.replace(/\n/g, '<br>');

// Injecter le message dans le template (remplacer un placeholder)
const htmlFinal = htmlTemplate.replace('{{MESSAGE_CONTENT}}', messageHtml);

// Retourner le HTML final avec les autres informations nécessaires
return {
  json: {
    ...$input.item(1).json,
    html: htmlFinal
  }
};

// Calcul d'un délai aléatoire pour éviter le spam

// Récupérer les paramètres de délai
const minDelay = $json.delayMinutes || 5;
const maxDelay = $json.maxDelayMinutes || 15;

// Calculer un délai aléatoire entre min et max
const randomDelay = Math.floor(Math.random() * (maxDelay - minDelay + 1)) + minDelay;

// Retourner le délai calculé
return {
  json: {
    ...$json,
    calculatedDelay: randomDelay
  }
};
