// Préparation des données pour l'analyse
const concertData = [];

for (const item of items) {
  try {
    // Extraction des données de la page Notion
    const properties = item.json.properties;
    const name = properties.Name.title[0]?.text.content || 'Concert sans nom';
    const date = properties.Date?.date?.start;
    const venue = properties.Venue?.rich_text[0]?.text.content || 'Lieu inconnu';
    const region = properties.Region?.select?.name || 'Région inconnue';

    // Ajout des données pour l'analyse
    concertData.push({
      json: {
        name,
        date,
        venue,
        region,
        pageId: item.json.id,
        url: item.json.url
      }
    });
  } catch (error) {
    // Gestion des erreurs
    console.log(`Erreur lors du traitement de l'élément: ${error.message}`);
  }
}

return concertData;

// Analyse des données de concerts
const concerts = items.map(item => item.json);

// Statistiques de base
const totalConcerts = concerts.length;
const regions = {};
const venues = {};
const monthlyDistribution = {};

// Analyse des données
concerts.forEach(concert => {
  // Analyse par région
  if (concert.region) {
    regions[concert.region] = (regions[concert.region] || 0) + 1;
  }

  // Analyse par salle
  if (concert.venue) {
    venues[concert.venue] = (venues[concert.venue] || 0) + 1;
  }

  // Analyse par mois
  if (concert.date) {
    const month = concert.date.substring(0, 7); // Format YYYY-MM
    monthlyDistribution[month] = (monthlyDistribution[month] || 0) + 1;
  }
});

// Préparation du rapport
const topRegions = Object.entries(regions)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 3)
  .map(([region, count]) => `${region}: ${count} concerts`);

const topVenues = Object.entries(venues)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 3)
  .map(([venue, count]) => `${venue}: ${count} concerts`);

const monthlyStats = Object.entries(monthlyDistribution)
  .sort(([monthA], [monthB]) => monthA.localeCompare(monthB))
  .map(([month, count]) => `${month}: ${count} concerts`);

// Création du rapport final
const report = {
  totalConcerts,
  topRegions,
  topVenues,
  monthlyStats,
  concertList: concerts.map(c => `${c.date}: ${c.venue} (${c.region})`)
};

// Formatage du rapport pour l'email
const emailContent = `
# Rapport d'Évaluation des Concerts

## Statistiques Globales
Total des concerts analysés: ${totalConcerts}

## Top Régions
${topRegions.join('\n')}

## Top Salles
${topVenues.join('\n')}

## Distribution Mensuelle
${monthlyStats.join('\n')}

## Liste des Concerts
${concerts.map(c => `- ${c.date}: ${c.venue} (${c.region})`).join('\n')}

## Recommandations
- Concentrer les efforts de prospection sur les régions les plus performantes
- Maintenir les relations avec les salles récurrentes
- Planifier les prochaines tournées en fonction de la saisonnalité observée
`;

return [{
  json: {
    report,
    emailContent
  }
}];
